mapper.select:
		SELECT d.table_name as database_name,r.product_source_code as product_source_code,
		sum(CASE r.operate_type when 3 then r.numbers else 0 end)sum1,
		sum(CASE r.operate_type when 1 then r.numbers else 0 end)sum2,
		sum(CASE r.operate_type when 2 then r.numbers else 0 end)sum3

		from rs_resources_statistics_database_use_hourly r
		left join
		datamanager d on d.product_source_code=r.product_source_code
		where 1=1
		
			and r.product_source_code= #{product_source_code}
		
		
			and r.source_db = #{source_db}
		
		
			
				and date = #{date}
				
					and r.hour  >=  #{startTime}
				
				
					and r.hour  <=  #{endTime}
				
			
			
				
					and r.date  >=  #{startTime}
				
				
					and r.date  <=  #{endTime}
				
			
		
		group by r.product_source_code
		limit #{pageNum},#{pageSize}

		SELECT d.table_name as database_name,r.product_source_code as product_source_code,
		sum(CASE r.operate_type when 3 then r.numbers else 0 end)sum1,
		sum(CASE r.operate_type when 1 then r.numbers else 0 end)sum2,
		sum(CASE r.operate_type when 2 then r.numbers else 0 end)sum3

		from rs_resources_statistics_database_use_hourly r
		left join
		datamanager d on d.product_source_code=r.product_source_code
		where 1=1
		
			and r.user_id = #{user_id}
		
		
			
				and r.product_source_code= #{product_source_code}
			
			
				and r.product_source_code in(select resource_id
				from wfks_pay_channel_resources wkfs where user_id = #{user_id} )
			
		
		
			and r.source_db = #{source_db}
		
		
			
				and date = #{date}
				
					and r.hour  >=  #{startTime}
				
				
					and r.hour  <=  #{endTime}
				
			
			
				
					and r.date  >=  #{startTime}
				
				
					and r.date  <=  #{endTime}
				
			
		
		group by r.product_source_code
		limit #{pageNum},#{pageSize}

SELECT d.table_name as database_name,r.product_source_code as product_source_code,
sum(CASE r.operate_type when 3 then r.numbers else 0 end)sum1,
sum(CASE r.operate_type when 1 then r.numbers else 0 end)sum2,
sum(CASE r.operate_type when 2 then r.numbers else 0 end)sum3
from rs_resources_statistics_database_use_hourly r
left join
datamanager d on d.product_source_code=r.product_source_code
where 1=1

	
		and r.product_source_code= #{product_source_code}
	
	
		and r.product_source_code in(select resource_id
		from wfks_pay_channel_resources wkfs where user_id in
		
			#{item}
		
		)
	


	and r.source_db = #{source_db}


	
		and date = #{date}
		
			and r.hour  >=  #{startTime}
		
		
			and r.hour  <=  #{endTime}
		
	
	
		
			and r.date  >=  #{startTime}
		
		
			and r.date  <=  #{endTime}
		
	

group by r.product_source_code
limit #{pageNum},#{pageSize}

