		SELECT operate_type
		from rs_resources_statistics_database_use_hourly
		WHERE 1=1
		
  			and institution=#{institution_name}
	  	
	  	
	  		and user_id = #{user_id}
	  	
	  	
  			and product_source_code= #{product_source_code}
  		
  		
  			and source_db = #{source_db}
  		
  		
  			and date = #{date}
  		
	  	
	  		and date  >=  #{startTime}
	  	
	  	
	  		and date  <=  #{endTime}
	  	
	  	
	  		and operate_type in
	  		
	  			#{item}
	  		
	  	
	  	
	  		and product_source_code in
	  		
	  			#{item}
	  		
	  	

		GROUP BY operate_type;

