mapper.select:
SELECT source_type_name as resource_type_code,
	sum(CASE operate_type when 1 then numbers else 0 end) sum1,
	sum(CASE operate_type when 2 then numbers else 0 end) sum2,
	sum(CASE operate_type when 3 then numbers else 0 end) sum3,
	sum(CASE operate_type when 4 then numbers else 0 end) sum4,
	sum(CASE operate_type when 5 then numbers else 0 end) sum5,
	sum(CASE operate_type when 6 then numbers else 0 end) sum6,
	sum(CASE operate_type when 7 then numbers else 0 end) sum7,
	sum(CASE operate_type when 8 then numbers else 0 end) sum8,
	sum(CASE operate_type when 9 then numbers else 0 end) sum9

	from rs_resources_statistics_sourcetype_use_hourly
	where 1=1

	 and source_db=#{res.source_db}


	 and product_source_code=#{res.product_source_code}


	 and source_type_name=#{res.sourceTypeName}



		and date = #{res.date}

			and hour  >=  #{startTime}


			and hour  <=  #{endTime}




			and date  >=  #{startTime}


			and date  <=  #{endTime}



	group by source_type_name order by sum3
	limit #{pageNum},#{pageSize}

SELECT source_type_name as resource_type_code,
sum(CASE operate_type when 1 then numbers else 0 end) sum1,
sum(CASE operate_type when 2 then numbers else 0 end) sum2,
sum(CASE operate_type when 3 then numbers else 0 end) sum3,
sum(CASE operate_type when 4 then numbers else 0 end) sum4,
sum(CASE operate_type when 5 then numbers else 0 end) sum5,
sum(CASE operate_type when 6 then numbers else 0 end) sum6,
sum(CASE operate_type when 7 then numbers else 0 end) sum7,
sum(CASE operate_type when 8 then numbers else 0 end) sum8,
sum(CASE operate_type when 9 then numbers else 0 end) sum9

from rs_resources_statistics_sourcetype_use_hourly
where 1=1

	and user_id=#{res.userId}


	and source_db=#{res.source_db}



		and product_source_code= #{res.product_source_code}


		and product_source_code in(select resource_id
		from wfks_pay_channel_resources wkfs where user_id = #{res.userId} GROUP BY resource_id)



	and source_type_name=#{res.sourceTypeName}



		and date = #{res.date}

			and hour  >=  #{startTime}


			and hour  <=  #{endTime}




			and date  >=  #{startTime}


			and date  <=  #{endTime}



group by source_type_name order by sum3
limit #{pageNum},#{pageSize}

