mapper.select:
SELECT access_type, sum(CASE type when 1 then numbers else 0 end) sum1, sum(CASE type when 2 then numbers else 0 end) sum2, sum(CASE type when 3 then numbers else 0 end) sum3, sum(CASE type when 4 then numbers else 0 end) sum4, sum(CASE type when 5 then numbers else 0 end) sum5, sum(CASE type when 6 then numbers else 0 end) sum6, sum(CASE type when 7 then numbers else 0 end) sum7, sum(CASE type when 8 then numbers else 0 end) sum8
from rs_source_analysis_all_source_hourly WHERE 1=1 and access_type is not null
and date=#{date}
and hour >= #{startTime}
and hour <= #{endTime}
GROUP BY access_type order by sum1 limit #{pageNum},#{pageSize};;

SELECT access_type, sum(CASE type when 1 then numbers else 0 end) sum1, sum(CASE type when 2 then numbers else 0 end) sum2, sum(CASE type when 3 then numbers else 0 end) sum3, sum(CASE type when 4 then numbers else 0 end) sum4, sum(CASE type when 5 then numbers else 0 end) sum5, sum(CASE type when 6 then numbers else 0 end) sum6, sum(CASE type when 7 then numbers else 0 end) sum7, sum(CASE type when 8 then numbers else 0 end) sum8
from rs_source_analysis_all_source_hourly WHERE 1=1 and access_type is not null
and date=#{date}
and hour >= #{startTime}
and hour <= #{endTime}
GROUP BY access_type order by sum1;

		SELECT access_type,type,date,SUM(numbers) sum from rs_source_analysis_all_source_hourly WHERE 1=1 
		and access_type is not null
		
  		and type=#{type}
	  	
	  	
  		and date=#{date}
   		
	  	
  		and hour  >=  #{startTime}
   		
   		
   		and hour  <=  #{endTime}
   		
	  	
		GROUP BY access_type,type ORDER BY sum DESC;

		SELECT access_type,
		(CASE sum(CASE type when #{type2} then numbers else 0 end) when 0 THEN 0 
		ELSE (sum(CASE type when #{type1} then numbers else 0 end)/sum(CASE type when #{type2} then numbers else 0 end)) END)sum

		from rs_source_analysis_all_source_hourly WHERE 1=1 
		and access_type is not null
	  	
  		and date=#{date}
   		
	  	
  		and hour  >=  #{startTime}
   		
   		
   		and hour  <=  #{endTime}
   		
	  	
		GROUP BY access_type ORDER BY sum DESC limit 0,10;

		SELECT
		(CASE sum(CASE type when #{type2} then numbers else 0 end) when 0 THEN 0 
		ELSE (sum(CASE type when #{type1} then numbers else 0 end)/sum(CASE type when #{type2} then numbers else 0 end)) END)sum

		from rs_source_analysis_all_source_hourly WHERE 1=1 
		and access_type is not null
  		
  		and date=#{date}
   		
	  	
  		and hour  >=  #{startTime}
   		
   		
   		and hour  <=  #{endTime}

		SELECT access_type from rs_source_analysis_all_source_hourly WHERE 1=1 
		and access_type is not null
		
  		and type=#{type}
	  	
	  	
  		and date=#{date}
   		
	  	
  		and hour  >=  #{startTime}
   		
   		
   		and hour  <=  #{endTime}
   		
	  	
		GROUP BY access_type;

		SELECT numbers from rs_source_analysis_all_source_hourly WHERE 1=1 
		and access_type is not null
		
  		and access_type=#{access_type}
	  	
		
  		and type=#{type}
	  	
	  	
  		and date=#{date}
   		
	  	
  		and hour  >=  #{startTime}
   		
   		
   		and hour  <=  #{endTime}
   		
	  	
		GROUP BY access_type,hour,type;

		SELECT hour from rs_source_analysis_all_source_hourly WHERE 1=1 
		and access_type is not null
		
  		and type=#{type}
	  	
	  	
  		and date=#{date}
   		
	  	
  		and hour  >=  #{startTime}
   		
   		
   		and hour  <=  #{endTime}
   		
	  	
		GROUP BY hour;
