SELECT link_host, sum(CASE type when 1 then numbers else 0 end) sum1, sum(CASE type when 2 then numbers else 0 end) sum2, sum(CASE type when 3 then numbers else 0 end) sum3, sum(CASE type when 4 then numbers else 0 end) sum4, sum(CASE type when 5 then numbers else 0 end) sum5, sum(CASE type when 6 then numbers else 0 end) sum6, sum(CASE type when 7 then numbers else 0 end) sum7, sum(CASE type when 8 then numbers else 0 end) sum8
from rs_source_analysis_external_linkhost_hourly WHERE 1=1 and link_host <> ''
and date=#{date}
and hour >= #{startTime}
and hour <= #{endTime}
GROUP BY link_host order by sum1 limit #{pageNum},#{pageSize};

