		SELECT link_host,type,date,SUM(numbers)sum from rs_source_analysis_external_linkhost_hourly WHERE 1=1 
		and  link_host <> ''
		
  		and type=#{type}
	  	
	  	
  		and date=#{date}
	  	
	  	
  		and hour  >=  #{startTime}
   		
   		
   		and hour  <=  #{endTime}
   		
	  	
		GROUP BY link_host,type ORDER BY sum DESC;

		SELECT link_host,type,date,SUM(numbers)sum from rs_source_analysis_external_linkhost_hourly WHERE 1=1 
		and  link_host <> ''
		
