SELECT (CASE sum(CASE type when #{type2} then numbers else 0 end) when 0 THEN 0 ELSE (sum(CASE type when #{type1} then numbers else 0 end)/sum(CASE type when #{type2} then numbers else 0 end)) END)sum
from rs_source_analysis_external_linkhost_hourly WHERE 1=1 and link_host <> ''
and date >= #{startTime}
and date <= #{endTime}

