mapper.select:
select * from rs_website_survey_basic_quota_hourly
where 1=1 

	and date >= #{startTime}


	and date <= #{endTime}


	and type = #{type}

 order by hour

		select * from rs_website_survey_basic_quota_hourly where 1=1
		
 	  		 and date >= #{startTime} 
		
		
 	 		 and date <= #{endTime} 
		
		
			and type = #{type}
		
		order by date

SELECT hour, MAX(CASE type WHEN 1 THEN numbers ELSE 0 END) 网站浏览量, MAX(CASE type WHEN 2 THEN numbers ELSE 0 END) 访问次数, MAX(CASE type WHEN 3 THEN numbers ELSE 0 END) 独立访客数, MAX(CASE type WHEN 4 THEN numbers ELSE 0 END) 新增访客, MAX(CASE type WHEN 5 THEN numbers ELSE 0 END) 独立IP数, MAX(CASE type WHEN 6 THEN numbers ELSE 0 END) 平均访问页面, MAX(CASE type WHEN 8 THEN numbers ELSE 0 END) 平均访问时长, MAX(CASE type WHEN 10 THEN numbers ELSE 0 END) 跳出率 FROM rs_website_survey_basic_quota_hourly where 1=1
and date >= #{startTime}
and date <= #{endTime}
GROUP BY hour
limit #{pagenum},#{pagesize}

SELECT date, MAX(CASE type WHEN 1 THEN numbers ELSE 0 END) 网站浏览量, MAX(CASE type WHEN 2 THEN numbers ELSE 0 END) 访问次数, MAX(CASE type WHEN 3 THEN numbers ELSE 0 END) 独立访客数, MAX(CASE type WHEN 4 THEN numbers ELSE 0 END) 新增访客, MAX(CASE type WHEN 5 THEN numbers ELSE 0 END) 独立IP数, MAX(CASE type WHEN 6 THEN numbers ELSE 0 END) 平均访问页面, MAX(CASE type WHEN 8 THEN numbers ELSE 0 END) 平均访问时长, MAX(CASE type WHEN 10 THEN numbers ELSE 0 END) 跳出率 FROM rs_website_survey_basic_quota_hourly where 1=1
and date >= #{startTime}
and date <= #{endTime}
GROUP BY date
limit #{pagenum},#{pagesize}

select count(DISTINCT(hour)) from rs_website_survey_basic_quota_hourly
where 1=1

	 and date >= #{startTime} 


	 and date <= #{endTime}

		select type,SUM(numbers) num FROM rs_website_survey_basic_quota_hourly 
			 where 1=1
		 
		 	and date >= #{startTime}
		 
 		
 			and date <= #{endTime}
 		
			GROUP BY type
