mapper.select:
SELECT engine_name, sum(CASE type when 1 then numbers else 0 end) sum1, sum(CASE type when 2 then numbers else 0 end) sum2, sum(CASE type when 3 then numbers else 0 end) sum3, sum(CASE type when 4 then numbers else 0 end) sum4, sum(CASE type when 5 then numbers else 0 end) sum5, sum(CASE type when 6 then numbers else 0 end) sum6, sum(CASE type when 7 then numbers else 0 end) sum7, sum(CASE type when 8 then numbers else 0 end) sum8
from rs_source_analysis_search_engines_daily WHERE 1=1
and date >= #{startTime}
and date <= #{endTime}
GROUP BY engine_name order by sum1 limit #{pageNum},#{pageSize};

SELECT engine_name, sum(CASE type when 1 then numbers else 0 end) sum1, sum(CASE type when 2 then numbers else 0 end) sum2, sum(CASE type when 3 then numbers else 0 end) sum3, sum(CASE type when 4 then numbers else 0 end) sum4, sum(CASE type when 5 then numbers else 0 end) sum5, sum(CASE type when 6 then numbers else 0 end) sum6, sum(CASE type when 7 then numbers else 0 end) sum7, sum(CASE type when 8 then numbers else 0 end) sum8
from rs_source_analysis_search_engines_daily WHERE 1=1
and date >= #{startTime}
and date <= #{endTime}
GROUP BY engine_name order by sum1;

		SELECT engine_name,type,date,SUM(numbers)sum from rs_source_analysis_search_engines_daily WHERE 1=1 
		
  		and type=#{type}
	  	
	  	
  		and date  >=  #{startTime}
   		
   		
   		and date  <=  #{endTime}
   		
	  	
		GROUP BY ENGINE_name,type ORDER BY sum DESC;

		SELECT engine_name,
		(CASE sum(CASE type when #{type2} then numbers else 0 end) when 0 THEN 0 
		ELSE (sum(CASE type when #{type1} then numbers else 0 end)/sum(CASE type when #{type2} then numbers else 0 end)) END)sum

		from rs_source_analysis_search_engines_daily WHERE 1=1 
	  	
  		and date  >=  #{startTime}
   		
   		
   		and date  <=  #{endTime}
   		
	  	
		GROUP BY ENGINE_name ORDER BY sum DESC limit 0,10;

		SELECT
		(CASE sum(CASE type when #{type2} then numbers else 0 end) when 0 THEN 0 
		ELSE (sum(CASE type when #{type1} then numbers else 0 end)/sum(CASE type when #{type2} then numbers else 0 end)) END)sum

		from rs_source_analysis_search_engines_daily WHERE 1=1 
	  	
  		and date  >=  #{startTime}
   		
   		
   		and date  <=  #{endTime}

		SELECT engine_name from rs_source_analysis_search_engines_daily WHERE 1=1 
		
		
  		and type=#{type}
	  	
	  	
  		and date  >=  #{startTime}
   		
   		
   		and date  <=  #{endTime}
   		
	  	
		GROUP BY engine_name;

		SELECT numbers from rs_source_analysis_search_engines_daily WHERE 1=1 
		
		
  		and engine_name=#{engine_name}
	  	
		
  		and type=#{type}
	  	
	  	
  		and date  >=  #{startTime}
   		
   		
   		and date  <=  #{endTime}
   		
	  	
		GROUP BY engine_name,date,type;

		SELECT date from rs_source_analysis_search_engines_daily WHERE 1=1 
		
		
  		and type=#{type}
	  	
	  	
  		and date  >=  #{startTime}
   		
   		
   		and date  <=  #{endTime}
   		
	  	
		GROUP BY date;
